	public $string = NULL;

    public function WordJumble($num=3,$prepend=NULL,$integer=TRUE) {
        $rand_keys = array_rand($this->words, $num);
        $string = $prepend;
        for ($i=0; $i < $num; $i++) {
        	$string .= ($i == 0)? $this->words[$rand_keys[$i]]:ucfirst($this->words[$rand_keys[$i]]);
        }
        $string .= ($integer)? rand(1,9):NULL;
        $this->string = $string;
    }
